@Composable
fun BlogListScreen(modifier: Modifier = Modifier,
                   state: BlogListState,
                   event:Flow<BlogListEvent >,
                   onBlogCardClick:(Int)->Unit
) {
    val context=   LocalContext.current
    LaunchedEffect (key1 =  Unit)
    {
        event.collect{
            event->
            when(event)
            {
                is BlogListEvent.Error -> {
                    Toast.makeText(context,event.error,Toast.LENGTH_SHORT).show()
                }
            }
        }
    }

    Column(
        modifier = modifier.fillMaxSize()
    ) {
        BlogListTopbar()
       LazyVerticalGrid(
           columns = GridCells.Adaptive(300.dp),
           contentPadding = PaddingValues(10.dp),
           verticalArrangement = Arrangement.spacedBy(10.dp),
           horizontalArrangement = Arrangement.spacedBy(10.dp)
       ) {
          
           items(state.blogs)
           {blog->
               BlogCard(
                   modifier = Modifier.clickable { onBlogCardClick(blog.id) },
                   blog=blog)
           }
       }

    }
}

@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun BlogListTopbar(modifier: Modifier = Modifier) {
    TopAppBar(
        windowInsets = WindowInsets(0),
        modifier = modifier,
        title = { Text("Android Blogs ") }
    )

}

@PreviewScreenSizes
@Composable
private fun Blogscreen() {
    val list = listOf(Blog(
        id = 1,
        title = "Andkorids repac",
        thumbnailUrl = "data:image/jpeg;base64,",
        contantUrl = "",
        contant = ""
    ))
    BlogListScreen(state = BlogListState(blogs = list), event = emptyFlow(), onBlogCardClick = {})

}